using UnityEngine;

namespace ZeroX.FsmSystem.Editors
{
    public class TextureBase64
    {
        public string base64;
        private int width = 1;
        private int height = 1;
        private Texture2D textureCached;

        public TextureBase64(int width, int height, string base64)
        {
            this.base64 = base64;
            this.width = width;
            this.height = height;
        }

        public Texture2D Value
        {
            get
            {
                if (textureCached == null)
                {
                    var bytes = System.Convert.FromBase64String(base64);
                    textureCached = new Texture2D(width, height);
                    textureCached.LoadImage(bytes);
                }
                
                return textureCached;
            }
        }
    }
    
    public static class TextureLibrary
    {
        public static readonly TextureBase64 LeftArrow = new TextureBase64(64, 64, "");
        public static readonly TextureBase64 RightArrow = new TextureBase64(64, 64, "");
        
        public static readonly TextureBase64 UpArrow = new TextureBase64(64, 64, "");
        public static readonly TextureBase64 DownArrow = new TextureBase64(64, 64, "");
    }
}